var Component = require('../index');
var assert = require('assert');

var component = new Component();

assert.throws(
  function() {
    component.setPlatform(undefined);
  },
  /undefined/
);

var Platform = function(name) {
  this.name = name;
}
var platform = new Platform('test computer');

component.setPlatform(platform);

assert(component.getPlatform() === platform);

var Platform = function() {}
var platform = new Platform();
component.setPlatform(platform);
