var Logger = require('../index');

var logger = new Logger({name: 'testLogger', debug: true});

logger.info('test');

logger.info('this is a test', 'with detail', 'and more detail', 'and even more detail');

logger.debug = false;

logger.info('this is a test you will not see', 'with detail', 'and more detail');

logger.debug = true;

logger.info('now you see it', 'with detail', 'and more detail');

function Application(options) {
  options = options || {};
  this.name = options.name || 'Application';
  this.debug = options.debug || true;
  this.logger = options.logger || new Logger({name: 'applicationLogger', debug: this.debug});
}

Application.prototype.info = function() {
  this.logger.info.apply(this,arguments);
}
Application.prototype.warn = function() {
  this.logger.warn.apply(this,arguments);
}
Application.prototype.error = function() {
  this.logger.error.apply(this,arguments);
}

var app = new Application();

app.info('testing','one','two','three');
app.warn('pay attention to this','ok?','you have been','warned');
app.error('hold it','this is bad','we stopped','everything');

app.info(1,2.0,3.33333333,4.4444444,5.5555);
